import PySimpleGUI as sg
import requests

# Theme
sg.theme('Topanga')

text = "Zaczynamy?"

image = b''
DISPLAY_TIME_MILLISECONDS = 2000

sg.Window('Tymbark - hasla', [[sg.Image(data=image)]], keep_on_top=True).read(timeout=DISPLAY_TIME_MILLISECONDS, close=True)

i = 0
while i < 1:
    layout = [[sg.Text(text)], [sg.Button("Haslo")]]

    # Create the window
    window = sg.Window("Tymbark - hasla", layout, margins=(100, 100), element_justification='center')

    # Create an event loop
    while True:
        event, values = window.read()

        if event == "Haslo":
            # Truth
            haslo = requests.get("https://tymbark-api.k0pa.repl.co")
            haslo = haslo.json()
            text = haslo['haslo']
            break

        if event == sg.WIN_CLOSED:
            i = 1
            break

    window.close()